import cv2import argparsedef main(args):    # 入力画像の読み込み    img = cv2.imread(args.img_path)    # カスケード型識別器の読み込み    cascade_path="/Users/tanimu//.pyenv/versions/miniconda3-latest/pkgs/opencv-3.3.1-py36h60a5f38_1/share/OpenCV/haarcascades/haarcascade_frontalface_default.xml"    cascade = cv2.CascadeClassifier(cascade_path)    # グレースケール変換    # gray = cv2.cvtColor(img, cv2.COLOR_BGR2GRAY)    # 顔領域の探索    people = cascade.detectMultiScale2(img, scaleFactor=1.1, minNeighbors=3, minSize=(30, 30))    print(people)    # 顔領域を赤色の矩形で囲む    for (x, y, w, h) in people:        cv2.rectangle(img, (x, y), (x + w, y+h), (0,0,200), 3)    # 結果を出力    cv2.imshow("images/result.jpg",img)if __name__ == '__main__':    parser = argparse.ArgumentParser(description='input image path')    parser.add_argument('--img_path', type=str, default='input.img', help='input image path')    args = parser.parse_args()    main(args)